import React from 'react';
import './Breadcrum.css';
import arrow_icon from '../Assets/breadcrum_arrow.png'; 

const Breadcrum = (props) => {
    const { product } = props;


    if (!product) {
        return null; // or a default breadcrumb, depending on your UI requirements
    }

    return (
        <div className='breadcrum'>
            HOME <img src={arrow_icon} alt="" /> SHOP <img src={arrow_icon} alt="" /> {product.category} <img src={arrow_icon} alt=""/> {product.name} {product.image}
        </div>
    );
}

export default Breadcrum;
